info.onCountdownEnd(function () {
    if (level == 1 && info.score() <= 50) {
        nextLevel()
    } else if (level == 2 && info.score() <= 50) {
        nextLevel()
    } else {
        level = 4
        level_4()
    }
    winConditionMet(3)
    gameOverCondition(4)
})
scene.onHitWall(SpriteKind.DårligAffald, function (sprite, location) {
    sprites.destroy(sprite)
})
sprites.onCreated(SpriteKind.GodAffald, function (sprite) {
    tiles.placeOnTile(sprite, tiles.getTileLocation(randint(0, 8), 0))
    sprite.setVelocity(0, 80)
})
function nextLevel () {
    level += 1
    if (level == 2) {
        Level2()
    } else if (level == 3) {
        Level3()
    }
}
sprites.onOverlap(SpriteKind.Player, SpriteKind.DårligAffald, function (sprite, otherSprite) {
    info.changeScoreBy(15)
    if (level == 4) {
        info.changeScoreBy(50)
    }
    sprites.destroy(otherSprite)
})
function winConditionMet (num: number) {
    if (level == num && info.score() <= 50) {
        game.setGameOverEffect(true, effects.starField)
    }
}
